import React from 'react';
import './index.css';

const ScriptView = ({ generatedScript, scenes }) => {
  return (
    <div className="script-view-container">
      <h1 className="title">Generated Script</h1>
      {scenes.map((scene, index) => (
        <div className="scene-container" key={index}>
          <h2 className="scene-title">Scene {index + 1}</h2>
          <textarea className="generated-script" value={scene} readOnly cols="50" rows="10"/>
        </div>
      ))}
    </div>
  );
};

export default ScriptView;
