import React from 'react';
import './index.css';

const ScriptView = ({ generatedScript, scenes }) => {
  return (
    <div className="script-view-container">
      <h1 className="title">Generated Script</h1>
      <textarea className="generated-script" value={generatedScript} readOnly cols="50" rows="20"/>

      <h2 className="title">Scenes</h2>
      <div className="scenes-container">
        <div className="scene-item">
          <h3>Scene 1</h3>
          <p>{scenes.scene1}</p>
        </div>
        <div className="scene-item">
          <h3>Scene 2</h3>
          <p>{scenes.scene2}</p>
        </div>
        <div className="scene-item">
          <h3>Scene 3</h3>
          <p>{scenes.scene3}</p>
        </div>
        <div className="scene-item">
          <h3>Scene 4</h3>
          <p>{scenes.scene4}</p>
        </div>
      </div>
    </div>
  );
};


export default ScriptView;
