import React, { useState } from 'react';

const HomePage = () => {
  const [title, setTitle] = useState('');
  const [genre, setGenre] = useState('');
  const [description, setDescription] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  const handleSubmit = (event) => {
    event.preventDefault();
    setIsLoading(true);

    // Use AI to generate script and visuals based on user input
    generateShortFilm(title, genre, description)
      .then((result) => {
        setIsLoading(false);
        // Display result to user
      })
      .catch((error) => {
        setIsLoading(false);
        // Display error message to user
      });
  };

  return (
    <div className="container">
      <h1>Welcome to the Short Movie Creator</h1>
      <p>
        Use our AI tools to help generate scripts and visuals for your short movie.
        Simply enter your ideas and preferences, and we'll do the rest!
      </p>
      <form onSubmit={handleSubmit}>
        <label htmlFor="title">Title:</label>
        <input
          type="text"
          id="title"
          name="title"
          value={title}
          onChange={(event) => setTitle(event.target.value)}
        />
        <br />
        <label htmlFor="genre">Genre:</label>
        <select
          id="genre"
          name="genre"
          value={genre}
          onChange={(event) => setGenre(event.target.value)}
        >
          <option value="action">Action</option>
          <option value="comedy">Comedy</option>
          <option value="drama">Drama</option>
          <option value="horror">Horror</option>
        </select>
        <br />
        <label htmlFor="description">Description:</label>
        <textarea
          id="description"
          name="description"
          value={description}
          onChange={(event) => setDescription(event.target.value)}
        />
        <br />
        {isLoading ? (
          <span>Loading...</span>
        ) : (
          <button type="submit">Create Movie</button>
        )}
      </form>
    </div>
  );
};

export default HomePage;
