import React, { useState } from 'react';
import './HomePage.css';
import axios from 'axios';


const HomePage = () => {
  const [title, setTitle] = useState('');
  const [genre, setGenre] = useState('');
  const [description, setDescription] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  const handleSubmit = (event) => {
    event.preventDefault();
    setIsLoading(true);

    // Use AI to generate script and visuals based on user input
    // generateShortFilm(title, genre, description)
    //   .then((result) => {
    //     setIsLoading(false);
    //     // Display result to user
    //   })
    //   .catch((error) => {
    //     setIsLoading(false);
    //     // Display error message to user
    //   });
  };

  return (
    <div className="container">
      <h1 className="title">Welcome to the Short Movie Creator</h1>
      <div className="image-container">
        {/* Add an image or video showcasing the product here */}
      </div>
      <p className="description">
        Use our AI tools to help generate scripts and visuals for your short movie.
        Simply enter your ideas and preferences, and we'll do the rest!
      </p>
      <div className="image-container">
        {/* Add an image or video showcasing the product here */}
      </div>
      <form className="form" onSubmit={handleSubmit}>
        <label htmlFor="title" className="label">
          Title:
        </label>
        <input
          type="text"
          id="title"
          name="title"
          value={title}
          onChange={(event) => setTitle(event.target.value)}
          className="input"
        />
        <br />
        <label htmlFor="genre" className="label">
          Genre:
        </label>
        <select
          id="genre"
          name="genre"
          value={genre}
          onChange={(event) => setGenre(event.target.value)}
          className="select"
        >
          <option value="action">Action</option>
          <option value="comedy">Comedy</option>
          <option value="drama">Drama</option>
          <option value="horror">Horror</option>
        </select>
        <br />
        <label htmlFor="description" className="label">
          Description:
        </label>
        <textarea
          id="description"
          name="description"
          value={description}
          onChange={(event) => setDescription(event.target.value)}
          className="textarea"
        />
        <br />
        {isLoading ? (
          <span className="loading">Loading...</span>
        ) : (
          <button type="submit" className="button">
            Create Movie
          </button>
        )}
      </form>
    </div>
  );
};

export default HomePage;
