var replicate = require('../main')

module.exports = function (source, dest) {
  // replicate("http://mikeal.iriscouch.com/hoodies", "http://mikeal.iriscouch.com/blank")
  var repl = new replicate.Replicator(
    { from:source
    , to:dest
    }
  )
  // require('http').globalAgent.maxSockets = 20
  repl.on('failed', function (obj) {
    if (!obj.resp) console.error('failed', obj.error)
    else console.error('failed', obj.id, obj.resp.statusCode, obj.error)
  })
  repl.on('pushed', function (obj) {
    console.log('pushed '+obj.id)
  })
  repl.continuous()
}
